import React, { useContext, useEffect } from 'react';
import Sidbar from '../component/sidebarcomponent/Sidbar';
import './Maincontainer.css';
import { Outlet, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { MyContext } from '../component/contextapi/CreateContext';

function Maincontainer() {
    const { id, setId } = useContext(MyContext);
    const navigate = useNavigate();

    useEffect(() => {
        const checkValidity = async () => {
            try {
                const response = await axios.get('http://localhost:3001/validitychecking', { withCredentials: true });
                console.log("Response data:", response.data.isLoggedIn);
                const isLoggedIn = response.data.isLoggedIn;
                setId(isLoggedIn);

                if (!isLoggedIn) {
                    navigate('/login');
                } else {
                    toast('Welcome'); // Show toast only once when logged in
                    navigate('/app/welcome'); // Navigate to welcome page after login
                }
            } catch (error) {
                console.error('Error checking validity:', error);
                // Optionally, handle error state or retry logic
            }
        };

        checkValidity();
    }, [setId, navigate]);

    // Ensure `id` and `navigate` are logged for debugging purposes
    useEffect(() => {
        console.log("Current id:", id);
    }, [id, navigate]);

    return (
        <div className='maincontainer'>
            {id ? <Sidbar /> : ""}
            <Outlet />
        </div>
    );
}

export default Maincontainer;
