import React, { useContext, useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import { IconButton } from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import SendOutlinedIcon from '@mui/icons-material/SendOutlined';
import DeleteIcon from '@mui/icons-material/Delete';
import { io } from 'socket.io-client';
import Messageself from '../messagecomponet/Messageself';
import Messageother from '../messagecomponet/Messageother';
import { MyContext, MyContextMessage } from '../contextapi/CreateContext';
import VideoCallIcon from '@mui/icons-material/VideoCall';
function Workarea() {
  const ENDPOINT = 'http://localhost:3001';
  const { id } = useContext(MyContext);
  const { state } = useLocation();
  const { message, setNewMessage } = useContext(MyContextMessage);
  const [text, setText] = useState('');
  const [socket, setSocket] = useState(null);
  const [roomId, setRoomId] = useState('');
  const [isInCall, setIsInCall] = useState(false);
  const [callRejected, setCallRejected] = useState(false);
  const [stream, setStream] = useState(null);
  const [otherUser, setOtherUser] = useState(null);
  const localVideoRef = useRef(null);
  const remoteVideoRef = useRef(null);
  useEffect(() => {
    const newSocket = io(ENDPOINT);
    setSocket(newSocket);


    newSocket.on('message received', (newMessage) => {
      console.log("Received new message:", newMessage);

      setNewMessage(prevMessages => [...prevMessages, newMessage]);
    });

    
    newSocket.emit('setup', id);
    newSocket.emit('join chat', '123'); 

 
    return () => {
      newSocket.disconnect();
    };
  }, [id, state.senderId, setNewMessage]);

  const sendMessage = () => {
    let currentTime = new Date();
    let hours = currentTime.getHours();
    let minutes = currentTime.getMinutes();
    let time = hours + ':' + minutes;
    
    const messageObj = {
      Chatid: id,
      Senderid: state.senderId,
      text: text,
      createdAt: time,
      updatedAt: time 
    };

    axios.post(`http://localhost:3001/newmessage`, messageObj)
      .then((response) => {

        if (socket) {
          socket.emit('new message', messageObj);
        }
        setText('');
        setNewMessage([...message, messageObj]); 
      })
      .catch((error) => {
        console.error('Error sending message:', error);
      });
  };
//calling functions

const startCall = () => {
  const roomId = '123'; 
  setRoomId(roomId);
  socket.emit('start video call', roomId);
  setIsInCall(true);
};

const answerCall = () => {
  socket.emit('answer video call', roomId);
  setIsInCall(true);
};

const rejectCall = () => {
  socket.emit('reject video call', roomId);
  setCallRejected(true);
};

const endCall = () => {
  socket.emit('end video call', roomId);
  setIsInCall(false);
};


useEffect(() => {
  if (!socket) return;

  socket.on('video call started', (roomId) => {
    setCallStartedMessage(`Video call started in room ${roomId}`);
  });

  socket.on('incoming video call', (roomId) => {
    setIsInCall(true);
  });

  socket.on('video call answered', () => {
    setIsInCall(true);
  });

  socket.on('video call rejected', () => {
    setCallRejected(true);
  });

  socket.on('video call ended', () => {
    setIsInCall(false);
  });

  return () => {
    socket.off('video call started');
    socket.off('incoming video call');
    socket.off('video call answered');
    socket.off('video call rejected');
    socket.off('video call ended');
  };
}, [socket]);
  return (
    <div className='main-workarea'>
      <div className="chat-head">
      
        <IconButton>
          <PersonIcon />
        </IconButton>
        <div className="user-info">
          <p className='title'>{state.Name}</p>
          <p className='timespan'>{state.time}</p>
        </div>
        <div className='delete'>
          <IconButton>
            <DeleteIcon />
          </IconButton>
          <IconButton>
            <VideoCallIcon onClick={()=>{startCall()}} />
          </IconButton>
        </div>
      </div>

      <div className="chat-container">
        {message && message.map((elem, index) => (
          <div key={index}>
            {id === elem.Chatid ?
              <Messageself dataprops={elem.text} dataprops2={elem.updatedAt} /> :
              <Messageother dataprops={elem.text} dataprops2={elem.updatedAt} />
            }
          </div>
        ))}
      </div>

      <div className="chat-input">
        <input onChange={(e) => { setText(e.target.value) }} placeholder='Type Text Here' value={text} name='text' className='input-text' type="text" />
        <div className='sendbutton'>
          <IconButton onClick={sendMessage}>
            <SendOutlinedIcon />
          </IconButton>
        </div>
      </div>
      





      <div>
      <h1>Video Call Component</h1>
      {isInCall ? (
        <div>
          <video ref={localVideoRef} autoPlay muted style={{ width: '300px', height: '200px' }}></video>
          <video ref={remoteVideoRef} autoPlay style={{ width: '300px', height: '200px' }}></video>
          <p>Room ID: {roomId}</p>
          <button onClick={endCall}>End Call</button>
        </div>
      ) : (
        <div>
          {!callRejected ? (
            <button onClick={startCall}>Start Call</button>
          ) : (
            <p>Call Rejected</p>
          )}
          <button onClick={answerCall}>Answer Call</button>
          <button onClick={rejectCall}>Reject Call</button>
        </div>
      )}
    </div>









    </div>
  );
}

export default Workarea;
