import React, { useContext, useEffect, useState } from 'react'
import './Workarea.css'
import DeleteIcon from '@mui/icons-material/Delete';
import { IconButton } from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import SendOutlinedIcon from '@mui/icons-material/SendOutlined';
import Messageother from '../messagecomponet/Messageother';
import Messageself from '../messagecomponet/Messageself';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import { MyContext } from '../contextapi/CreateContext';

function Workarea() {
  const { id, setId } = useContext(MyContext);
  const [data, setData] = useState(null);
  useEffect(() => {
    axios.get(`http://localhost:3001/finduserlogin/667a4ede4414838d6e40e7e6`).then((response)=>{
      console.log(response.data);
      setData(response.data.Name);
    })
  }, [])
  
  uses
  const {state} = useLocation();
  //(state.response)
  console.log(state.response)
  let dataofmessagecollection=state.response
  
 
  console.log("all details")
  console.log(dataofmessagecollection[0].Senderid);

 
  return (
    <div className='main-workarea'>

     <div className="chat-head">
       

        <IconButton>
           <PersonIcon/>
        </IconButton>
        <div className="user-info">
      <p className='title'>{data}</p>
      <p className='timespan'>12.00</p>
    </div>

       <div className='delete'>
        <IconButton>
        <DeleteIcon/>
      </IconButton>
       </div>
      
     </div>





     <div className="chat-container">
      {
        dataofmessagecollection.map((elem)=>{
          return(
            <div>
              {id==elem.Chatid ? 
              <Messageself dataprops={elem.text} />
             :<Messageother dataprops={elem.text}  />}
            </div>
          )
         
        })
      }
  
     </div>




     <div className="chat-input">

     
      <input onChange={(e)=>{settext(e.target.value)}} placeholder='Type Text Here' value={text} name='text' className='input-text' type="text" />
      <div className='sendbutton'>
          <IconButton>
           <SendOutlinedIcon/>
        </IconButton>
      </div>
    
     </div>

    </div>
  )
}

export default Workarea
