import { createSlice } from "@reduxjs/toolkit";

 export const Themetoogle=createSlice({
    name:'theme',
    initialState:true,
    themetoogle:(state)=>{
     state=!state;
    }
 })

export const {themetoogle}=Themetoogle.actions;
export default Theme.reducer
