import { createSlice } from "@reduxjs/toolkit";

 export const ThemeSlice=createSlice({
    name:'theme',
    initialState: {
      value: true,
    },
    ThemeToogler:(state)=>{
     state.va=!state
    },
 })

export const { ThemeToogler } = ThemeSlice.actions;
export default ThemeSlice.reducer
