import React, { useState } from 'react'
import './Sidbar.css'
import PermIdentityOutlinedIcon from '@mui/icons-material/PermIdentityOutlined';
import GroupAddOutlinedIcon from '@mui/icons-material/GroupAddOutlined';
import GroupOutlinedIcon from '@mui/icons-material/GroupOutlined';
import AddCircleOutlineOutlinedIcon from '@mui/icons-material/AddCircleOutlineOutlined';
import NightsStayOutlinedIcon from '@mui/icons-material/NightsStayOutlined';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import LightModeIcon from '@mui/icons-material/LightMode';
import { Icon, IconButton } from '@mui/material';
import { Navigate, useNavigate } from 'react-router-dom';
function Sidbar() {
  const [light, setlight] = useState(true)
  let navigate=useNavigate();
  const [conversation, setconversation] = useState([
    {
      name: 'manu',
      lastmessage: 'last message 1',
      timespan: 'today'
    },
    {
      name: 'charli',
      lastmessage: 'last message 1',
      timespan: 'today'
    },
    {
      name: 'aswin',
      lastmessage: 'last message 1',
      timespan: 'today'
    }

  ])
  return (
    <div className="main-sidebar">
      <div className={"sidebar-header"+(light==tr ? "" :"dark")} >
        <div>
          <IconButton>
            <PermIdentityOutlinedIcon />
          </IconButton>
        </div>

        <div className='forspace'>
          <IconButton onClick={()=>{navigate("Onlineuser")}}>
            <GroupOutlinedIcon  />
          </IconButton>
          <IconButton onClick={()=>{navigate("availablegroup")}}>
            <GroupAddOutlinedIcon />
          </IconButton>

          <IconButton>
            <AddCircleOutlineOutlinedIcon onClick={()=>{navigate("Groupcreating")}} />
          </IconButton>

          <IconButton onClick={()=>{
               setlight(light =>!light)
            }}>
              {!light && <NightsStayOutlinedIcon/>}
               {light && <LightModeIcon/>}
               
          </IconButton>
        </div>


      </div>


      <div className="side-bar-text">
        <IconButton>
          <SearchOutlinedIcon />
        </IconButton>

        <input className='textarea1' type="text" placeholder='Search' />
      </div>
      <div className="side-bar-container">
        {
          conversation.map((conversation) => {
            return (
              <div onClick={()=>{navigate('Workarea')}}  className='conversation-container' key={conversation.name}>
                <p className='con-icon'>{conversation.name[0]}</p>
                <p className='con-title'>{conversation.name}</p>
                <p className='con-lastmessage'>{conversation.lastmessage}</p>
                <p className='con-timestamp'>{conversation.timespan}</p>
              </div>

            )
          })
        }

      </div>
    </div>

  )
}

export default Sidbar
