import React, { useContext, useEffect, useState } from 'react'
import './Workarea.css'
import DeleteIcon from '@mui/icons-material/Delete';
import { IconButton } from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import SendOutlinedIcon from '@mui/icons-material/SendOutlined';
import Messageother from '../messagecomponet/Messageother';
import Messageself from '../messagecomponet/Messageself';
import { useLocation } from 'react-router-dom';
import axios from 'axios';

function Workarea() {
  var data=[];
  useEffect(() => {
    axios.get(`http://localhost:3001/finduserlogin/667a4ede4414838d6e40e7e6`).then((response)=>{
      console.log(response.data);
      data.push(response.data)
    })
  }, [])
  
   const [conversation, setconversation] = useState(
    {
      name: 'manu',
      lastmessage: 'last message 1',
      timespan: 'today'
    }
  )
  const {state} = useLocation();
  //(state.response)
  console.log(state.response)
  let dataofmessagecollection=state.response
  
 
  console.log("all details")
  console.log(dataofmessagecollection[0].Senderid);
  console.log(data)
  let full
  console.log(data[0])
  return (
    <div className='main-workarea'>

     <div className="chat-head">
       

        <IconButton>
           <PersonIcon/>
        </IconButton>
        <div className="user-info">
      <p className='title'>dsf</p>
      <p className='timespan'>{dataofmessagecollection[0].createdAt}</p>
    </div>

       <div className='delete'>
        <IconButton>
        <DeleteIcon/>
      </IconButton>
       </div>
      
     </div>





     <div className="chat-container">
   <Messageother /> 
   <Messageself/>
   <Messageother/> 
   <Messageself/>
   <Messageother/> 
   <Messageself/>

     </div>




     <div className="chat-input">

     
      <input placeholder='Type Text Here' className='input-text' type="text" />
      <div className='sendbutton'>
          <IconButton>
           <SendOutlinedIcon/>
        </IconButton>
      </div>
    
     </div>

    </div>
  )
}

export default Workarea
