import { useContext, useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import { IconButton } from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import SendOutlinedIcon from '@mui/icons-material/SendOutlined';
import DeleteIcon from '@mui/icons-material/Delete';
import { io } from 'socket.io-client';
import Messageself from '../messagecomponet/Messageself';
import Messageother from '../messagecomponet/Messageother';
import { MyContext, MyContextMessage } from '../contextapi/CreateContext';

function Workarea() {
  const ENDPOINT = 'http://localhost:3001';
  const { id } = useContext(MyContext);
  const { state } = useLocation();
  const { message, setNewMessage } = useContext(MyContextMessage);
  const [data, setData] = useState(null);
  const [text, setText] = useState('');

  useEffect(() => {
    // Fetch user data based on senderId from state
    axios.get(`http://localhost:3001/finduserlogin/${state.senderId}`)
      .then((response) => {
        setData(response.data.Name);
      })
      .catch((error) => {
        console.error('Error fetching user data:', error);
      });

    // Initialize socket connection
    const socket = io(ENDPOINT);
    socket.on('message received', (newMessage) => {
      // Update messages when a new message is received
      setNewMessage([...message, newMessage]);
    });

    return () => {
      socket.disconnect(); // Clean up socket connection on component unmount
    };
  }, [state.senderId, setNewMessage]); // Dependency array to re-run effect when senderId or setNewMessage changes

  const sendMessage = () => {
    const messageObj = {
      hatid: id,
      senderid: state.senderId,
      text: text
    };

    axios.post(`http://localhost:3001/newmessage`, messageObj)
      .then((response) => {
        const socket = io(ENDPOINT);
        socket.emit('new message', messageObj);
        setText('');
        setNewMessage([...message, messageObj]); // Update local state immediately
      })
      .catch((error) => {
        console.error('Error sending message:', error);
      });
  };

  return (
    <div className='main-workarea'>
      <div className="chat-head">
        <IconButton>
          <PersonIcon />
        </IconButton>
        <div className="user-info">
          <p className='title'>{state.Name}</p>
          <p className='timespan'>{state.time}</p>
        </div>
        <div className='delete'>
          <IconButton>
            <DeleteIcon />
          </IconButton>
        </div>
      </div>

      <div className="chat-container">
        {message && message.map((elem, index) => (
          <div key={index}>
            {id === elem.Chatid ?
              <Messageself dataprops={elem.text} dataprops2={elem.updatedAt} /> :
              <Messageother dataprops={elem.text} />
            }
          </div>
        ))}
      </div>

      <div className="chat-input">
        <input onChange={(e) => { setText(e.target.value) }} placeholder='Type Text Here' value={text} name='text' className='input-text' type="text" />
        <div className='sendbutton'>
          <IconButton onClick={sendMessage}>
            <SendOutlinedIcon />
          </IconButton>
        </div>
      </div>
    </div>
  );
}

export default Workarea;
