import React, { useState } from 'react'
import './Workarea.css'
import DeleteIcon from '@mui/icons-material/Delete';
import { IconButton } from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import SendOutlinedIcon from '@mui/icons-material/SendOutlined';
import Messageother from '../messagecomponet/Messageother';
import Messageself from '../messagecomponet/Messageself';
function Workarea() {
  useEffect(() => {
    axios.get('http://localhost:3001/validitychecking', { withCredentials: true })
    .then((response) => {
      console.log(response.data);
      if (!response.data.isLoggedIn) {
        navigate('/login');
      } else {
        toast('Welcome');
      }
    })
    .catch((error) => {
      console.error('Error checking validity:', error);
    });
    
  }, [])
   const [conversation, setconversation] = useState(
    {
      name: 'manu',
      lastmessage: 'last message 1',
      timespan: 'today'
    }
  )
  return (
    <div className='main-workarea'>

     <div className="chat-head">
       
        <IconButton>
           <PersonIcon/>
        </IconButton>
        <div className="user-info">
      <p className='title'>{conversation.name}</p>
      <p className='timespan'>{conversation.timespan}</p>
    </div>

       <div className='delete'>
        <IconButton>
        <DeleteIcon/>
      </IconButton>
       </div>
      
     </div>





     <div className="chat-container">
   <Messageother/> 
   <Messageself/>
   <Messageother/> 
   <Messageself/>
   <Messageother/> 
   <Messageself/>

     </div>




     <div className="chat-input">

     
      <input placeholder='Type Text Here' className='input-text' type="text" />
      <div className='sendbutton'>
          <IconButton>
           <SendOutlinedIcon/>
        </IconButton>
      </div>
    
     </div>

    </div>
  )
}

export default Workarea
