import React, { useContext, useEffect, useState } from 'react'
import './Workarea.css'
import DeleteIcon from '@mui/icons-material/Delete';
import { IconButton } from '@mui/material';
import PersonIcon from '@mui/icons-material/Person';
import SendOutlinedIcon from '@mui/icons-material/SendOutlined';
import Messageother from '../messagecomponet/Messageother';
import Messageself from '../messagecomponet/Messageself';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import { MyContext, MyContextMessage } from '../contextapi/CreateContext';
import { io, Socket } from "socket.io-client";

function Workarea() {
  var soket;
  var ENDPOINT='http://localhost:3001'
  const { id, setId } = useContext(MyContext);
  const [data, setData] = useState(null);
  const {state} = useLocation();
  useEffect(() => {
    axios.get(`http://localhost:3001/finduserlogin/${state.senderId}`).then((response)=>{
      console.log(response.data);
      setData(response.data.Name);
    })
  }, [])
  
  const [text, settext] = useState('')
  
  //(state.response)
  console.log(state.response)
  const { message,setNewMessage}=useContext(MyContextMessage)

  const dataofmessagecollection=message
  console.log("all details")
  
  function messagesend(){
   let messageobj={
      chatid:id,
      senderid:state.senderId,
      text:text
    }
    axios.post(`http://localhost:3001/newmessage`,messageobj).then((response)=>{
      soket=io(ENDPOINT)
      soket.emit("new message",messageobj)
      settext('');
    })
  }

  useEffect(() => {
    soket=io(ENDPOINT)
    soket.on('message recived',(newmsg)=>{
      console.log("happy happy happy  ", newmsg);
      setNewMessage([...message,newmsg])
      console.log("manuzzz");
    })
    return () => {
      soket.disconnect();
    };

 
  }, [])
  return (
    <div className='main-workarea'>

     <div className="chat-head">
       

        <IconButton>
           <PersonIcon/>
        </IconButton>
        <div className="user-info">
      <p className='title'>{state.Name}</p>
      <p className='timespan'>{state.time}</p>
    </div>

       <div className='delete'>
        <IconButton>
        <DeleteIcon/>
      </IconButton>
       </div>
      
     </div>





     <div className="chat-container">
      { dataofmessagecollection ?  dataofmessagecollection.map((elem)=>{
          return(
            <div>
              {id==elem.Chatid ? 
              <Messageself dataprops={elem.text} dataprops2={elem.updatedAt} />
             :<Messageother dataprops={elem.text}  />}
            </div>
          )
         
        }) :""     }
  
     </div>




     <div className="chat-input">

     
      <input onChange={(e)=>{settext(e.target.value)}} placeholder='Type Text Here' value={text} name='text' className='input-text' type="text" />
      <div className='sendbutton'>
          <IconButton onClick={messagesend}>
           <SendOutlinedIcon/>
        </IconButton>
      </div>
    
     </div>

    </div>
  )
}

export default Workarea
