import { createSlice } from "@reduxjs/toolkit";

 export const ThemeSlice=createSlice({
    name:'theme',
    initialState:true,
    Theme:(state)=>{
     state=!state;
    }
 })

export const { themetoogle2 } = ThemeSlice.actions;
export default ThemeSlice.reducer
