import React, { useContext, useEffect, useState } from 'react';
import './Sidbar.css';
import PermIdentityOutlinedIcon from '@mui/icons-material/PermIdentityOutlined';
import GroupAddOutlinedIcon from '@mui/icons-material/GroupAddOutlined';
import GroupOutlinedIcon from '@mui/icons-material/GroupOutlined';
import AddCircleOutlineOutlinedIcon from '@mui/icons-material/AddCircleOutlineOutlined';
import NightsStayOutlinedIcon from '@mui/icons-material/NightsStayOutlined';
import SearchOutlinedIcon from '@mui/icons-material/SearchOutlined';
import LightModeIcon from '@mui/icons-material/LightMode';
import { IconButton } from '@mui/material';
import LogoutIcon from '@mui/icons-material/Logout';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { io } from 'socket.io-client';
import { MyContext, MyContextMessage } from '../contextapi/CreateContext';

function Sidebar() {
  const ENDPOINT = 'http://localhost:3001';
  const { id } = useContext(MyContext);
  const { setNewMessage } = useContext(MyContextMessage);
  const [conversation, setConversation] = useState([]);
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const light = useSelector((state) => state.key);

  useEffect(() => {
    const socket = io(ENDPOINT);
    socket.emit('setup', id);

    socket.on('message received', (newMessage) => {
      console.log('Received new message:', newMessage);
      setNewMessage((prevMessages) => [...prevMessages, newMessage]);
    });

    return () => {
      socket.disconnect();
    };
  }, [id, setNewMessage]);

  useEffect(() => {
    axios.get(`http://localhost:3001/finduserchat/${id}`, { withCredentials: true })
      .then((response) => {
        console.log(response.data);
        setConversation(response.data);
      })
      .catch((error) => {
        console.error('Error fetching user chats:', error);
      });
  }, [id]);

  const handleConversationClick = (senderId, name, time) => {
    axios.get(`http://localhost:3001/findchatids/${id}/${senderId}`)
      .then((response) => {
        console.log('Fetched messages:', response.data);
        setNewMessage(response.data);

        const socket = io(ENDPOINT);
        socket.emit('join chat', response.data.Chatid); // Assuming response.data.Chatid is the chat room ID

        navigate('Workarea', { state: { senderId, Name: name, time } });
      })
      .catch((error) => {
        console.error('Error fetching chat messages:', error);
      });
  };

  const handleLogout = () => {
    axios.get(`http://localhost:3001/logot/${id}`, { withCredentials: true })
      .then(() => {
        navigate('/login');
        window.location.reload();
      })
      .catch((error) => {
        console.error('Error logging out:', error);
      });
  };

  return (
    <div className={`main-sidebar${light ? '' : ' dark1'}`}>
      <div className="sidebar-header">
        <div>
          <IconButton>
            <PermIdentityOutlinedIcon />
          </IconButton>
        </div>

        <div className="forspace">
          <IconButton onClick={() => { navigate('Onlineuser'); }}>
            <GroupOutlinedIcon />
          </IconButton>
          <IconButton onClick={() => { navigate('availablegroup'); }}>
            <GroupAddOutlinedIcon />
          </IconButton>
          <IconButton onClick={() => { navigate('Groupcreating'); }}>
            <AddCircleOutlineOutlinedIcon />
          </IconButton>

          <IconButton onClick={() => { dispatch(ThemeToogler()); }}>
            {!light && <NightsStayOutlinedIcon />}
            {light && <LightModeIcon />}
          </IconButton>

          <IconButton onClick={handleLogout}>
            <LogoutIcon />
          </IconButton>
        </div>
      </div>

      <div className="side-bar-text">
        <IconButton>
          <SearchOutlinedIcon />
        </IconButton>

        <input className={`textarea1${light ? '' : ' dark1'}`} type="text" placeholder="Search" />
      </div>

      <div className="side-bar-container">
        {conversation.map((conv) => (
          <div key={conv._id} onClick={() => handleConversationClick(conv._id, conv.Name, conv.edited)} className="conversation-container">
            <p className="con-icon">{conv.Name[0]}</p>
            <p className="con-title">{conv.Name}</p>
            <p className="con-lastmessage">lastmessage: {lastMessages[conv._id]}</p>
            <p className="con-timestamp">{lasttime[conv._id]}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Sidebar;
