import { createSlice } from "@reduxjs/toolkit";

 export const ThemeSlice=createSlice({
    name:'theme',
    initialState: {
      value: true,
    },
   reducers:{
    ThemeToogler:(state)=>{
     state.value=!state.value
    },
   }
 })

export const { ThemeToogler } = ThemeSlice.actions;
export default ThemeSlice.reducer
